// Normalize IPIP to T/p01 (A)
const ans = items[0].json.answers || [];
// dummy: assume domain sums in 0..1
function mapToT(v){return Math.max(0, Math.min(100, Math.round(50+10*(v-0.5)/0.1)));}
const t = {O: mapToT(0.63), C: mapToT(0.44), E: mapToT(0.52), A: mapToT(0.58), N: mapToT(0.41)};
const p01 = {O: 0.63, C: 0.44, E: 0.52, A: 0.58, N: 0.41};
const norm_version = 'Johnson2014';
const user_id = items[0].json.user_id || 1;
const administered_at = new Date().toISOString().slice(0,19).replace('T',' ');
return [{json: {user_id, administered_at, O_T:t.O, C_T:t.C, E_T:t.E, A_T:t.A, N_T:t.N, O_p01:p01.O, C_p01:p01.C, E_p01:p01.E, A_p01:p01.A, N_p01:p01.N, norm_version}}];
